-                       ((CThread)it.next()).resumedByTarget( detail, 
debugEvents );
+                       CThread thread = (CThread)it.next();
+                       ICDIThread resumptionThread = null;
+                       try {
+                               resumptionThread =
getCDITarget().getCurrentThread();
+                       }
+                       catch( CDIException e ) {
+                               // ignore
+                       }
