package org.eclipse.swt.snippets;

import org.eclipse.swt.SWT;
import org.eclipse.swt.graphics.Region;
import org.eclipse.swt.widgets.Display;
import org.eclipse.swt.widgets.Event;
import org.eclipse.swt.widgets.Listener;
import org.eclipse.swt.widgets.Shell;

public class OSXWindowRegionBug
{

	/**
	 * @param args
	 */
	public static void main(String[] args)
	{
		final Display display = new Display();
		final Shell shell = new Shell(display, SWT.NO_TRIM | SWT.NO_BACKGROUND | SWT.NO_REDRAW_RESIZE);
		shell.setBounds(100, 30, 100, 100);

		Region r = new Region(display);
		r.add(0,0,100,100);
		for(int i=10; i >=0; i--)
		{
			r.add(10+i, 10-i, 80-i, 1);
		}	
		shell.setRegion(r);
		shell.addListener(SWT.MouseUp, new Listener(){

			public void handleEvent(Event event)
			{
				shell.setLocation(shell.getLocation().x+10, shell.getLocation().y);
				Region region = new Region(display);
				for(int i=10; i >=0; i--)
				{
					region.add(10+i, 10-i, 80-i, 1);
					region.add(100,100,100,100);
				}
				shell.setRegion(region );
			}});

		shell.open();

		while(!shell.isDisposed())
		{
			if(!display.readAndDispatch())
			{

				display.sleep();
			}
		}
	}
}

