public class Deadlock {

    public Object lock1 = new Object();
    public Object lock2 = new Object();

    class Process1 implements Runnable {

            /* (non-Javadoc)
             * @see java.lang.Runnable#run()
             */
            public void run() {
                    synchronized (lock1) {
                            try {
                                    new Runnable() {
                                            public void run() {
                                                    foo();
                                            }
                                    }.run();
                                    Thread.sleep(3000);
                            } catch (InterruptedException e1) {
                            }
                            synchronized (lock2) {
                                    try {
                                            lock2.wait();
                                    } catch (InterruptedException e) {
                                    }
                            }
                            lock1.notifyAll();
                    }
            }


    }

    public String foo() {
            return "Foo";
    }

    class Process2 implements Runnable {

            /* (non-Javadoc)
             * @see java.lang.Runnable#run()
             */
            public void run() {
                    synchronized (lock2) {
                            synchronized (lock1) {
                                    try {
                                            lock1.wait();
                                    } catch (InterruptedException e) {
                                    }
                            }
                            lock2.notifyAll();
                    }
            }

    }
    public static void main(String[] args) {
            new Deadlock().lock();
    }


    /**
     * 
     */
    private void lock() {
            System.out.println("hello");

            ThreadGroup grp = new ThreadGroup("Sam's Group 1");
            Process1 process1 = new Process1();
            Thread thread1 = new Thread(grp, process1);
            Process2 process2 = new Process2();
            Thread thread2 = new Thread(process2);
            thread1.start();
            thread2.start();

    }
}

