        radio1.setLayoutData(gd);
        radio1.setSelection(false);

        Button radio2 = new Button(group, SWT.RADIO);
        radio2.setText("Radio2");
        gd = new GridData(SWT.BEGINNING, SWT.BEGINNING, false, false);
        radio2.setLayoutData(gd);
        radio2.setSelection(false);

        Composite innerComp = new Composite(comp, SWT.NONE);
        innerComp.setLayout(new GridLayout(2,false));

        // Radios in a composite have no selection
        Button compRadio1 = new Button(innerComp, SWT.RADIO);
        compRadio1.setText("CompRadio1");
        gd = new GridData(SWT.BEGINNING, SWT.BEGINNING, false, false);
        compRadio1.setLayoutData(gd);
        compRadio1.setSelection(false);

        Button compRadio2 = new Button(innerComp, SWT.RADIO);
        compRadio2.setText("CompRadio2");
        gd = new GridData(SWT.BEGINNING, SWT.BEGINNING, false, false);
        compRadio2.setLayoutData(gd);
        compRadio2.setSelection(false);


        shell.open();
        while (!shell.isDisposed()) {
            if (!display.readAndDispatch())
                display.sleep();
        }
        display.dispose();
    }
}

