import org.eclipse.swt.SWT;
import org.eclipse.swt.SWTError;
import org.eclipse.swt.graphics.Point;
import org.eclipse.swt.layout.GridLayout;
import org.eclipse.swt.widgets.Button;
import org.eclipse.swt.widgets.Combo;
import org.eclipse.swt.widgets.Display;
import org.eclipse.swt.widgets.Label;
import org.eclipse.swt.widgets.Shell;
import org.eclipse.swt.widgets.Text;

public class TestHandlesError {

    public static void main(String[] args) {
        int nbOfWidget = 0;
        int screen = 0, widget4Screen = 0;

        try{
            final Display display = Display.getDefault();
            for (screen=0; screen<40; screen++){            
                // 1) -- Create a simple screen and repeat it 40 times ----            
                Shell shell = new Shell(display);
                shell.setSize(new Point(276, 134));
                shell.setLayout(new GridLayout(4, true));
                for (widget4Screen=0; widget4Screen<60; widget4Screen++){
                    Label label = new Label(shell, SWT.NONE);
                    label.setText("Label");
                    nbOfWidget ++;

                    Text text = new Text(shell, SWT.BORDER);
                    text.setText("Text");
                    nbOfWidget ++;             

                    Combo combo = new Combo(shell, SWT.None);
                    combo.add("text 1");
                    combo.add("text 2");
                    combo.add("text 3");
                    combo.add("text 4");
                    combo.setText("Combo");
                    nbOfWidget ++;     

                    Button button = new Button(shell, SWT.None);
                    button.setText("Button "+screen+"/"+widget4Screen);
                    nbOfWidget ++;
                }                
                shell.open();            
            }

            while (!display.isDisposed()) {            
                if (!display.readAndDispatch())
                    display.sleep();                    
            }
        }catch(SWTError e){
            System.out.println("--> SWTError at screen "+screen+" on it's widget "+widget4Screen+ " (widget nb "+nbOfWidget+") :"+e.getMessage());
        }
    }
}


