package org.eclipse.jface.snippets.viewers;

import org.eclipse.jface.viewers.ITreeContentProvider;
import org.eclipse.jface.viewers.OwnerDrawLabelProvider;
import org.eclipse.jface.viewers.TreeViewer;
import org.eclipse.jface.viewers.TreeViewerColumn;
import org.eclipse.jface.viewers.Viewer;
import org.eclipse.jface.viewers.ViewerCell;
import org.eclipse.swt.SWT;
import org.eclipse.swt.events.SelectionAdapter;
import org.eclipse.swt.events.SelectionEvent;
import org.eclipse.swt.graphics.Color;
import org.eclipse.swt.graphics.GC;
import org.eclipse.swt.graphics.Rectangle;
import org.eclipse.swt.graphics.Region;
import org.eclipse.swt.layout.FillLayout;
import org.eclipse.swt.widgets.Display;
import org.eclipse.swt.widgets.Event;
import org.eclipse.swt.widgets.Menu;
import org.eclipse.swt.widgets.MenuItem;
import org.eclipse.swt.widgets.Shell;
import org.eclipse.swt.widgets.Tree;

import java.util.ArrayList;

public class Snippet002TreeViewer {
  private class MyContentProvider implements ITreeContentProvider {

    public Object[] getElements(Object inputElement) {
      return ((MyModel) inputElement).child.toArray();
    }

    public void dispose() {}

    public void inputChanged(Viewer viewer, Object oldInput, Object newInput) {}

    public Object[] getChildren(Object parentElement) {
      return new Object[0];
    }

    public Object getParent(Object element) {
      if (element == null) {
        return null;
      }

      return ((MyModel) element).parent;
    }

    public boolean hasChildren(Object element) {
      return false;
    }
  }

  public class MyModel {
    public MyModel parent;
    public ArrayList child = new ArrayList();
    public int counter;

    public MyModel(int counter, MyModel parent) {
      this.parent = parent;
      this.counter = counter;
    }

    public String toString() {
      String rv = "Item ";
      if (parent != null) {
        rv = parent.toString() + ".";
      }

      rv += counter;

      return rv;
    }
  }

  boolean isRedLabel = false;

  public Snippet002TreeViewer(Shell shell) {
    final TreeViewer v = new TreeViewer(shell, SWT.MULTI | SWT.FULL_SELECTION);
    OwnerDrawLabelProvider.setUpOwnerDraw(v);

    v.getTree().setHeaderVisible(true);

    TreeViewerColumn col = new TreeViewerColumn(v, SWT.None);
    col.setLabelProvider(new MyOwnerDrawLabelProvider(v.getTree()));
    col.getColumn().setWidth(800);

    v.setContentProvider(new MyContentProvider());
    final MyModel model = createModel();
    v.setInput(model);

    Menu m = new Menu(v.getTree());
    v.getTree().setMenu(m);
    MenuItem i = new MenuItem(m, SWT.NONE);
    i.setText("Red Label!");
    i.addSelectionListener(new SelectionAdapter() {
      public void widgetSelected(SelectionEvent e) {
        isRedLabel = true;
        v.update(new Object[] { v.getTree().getSelection()[0].getData() }, null);
      }
    });
  }

  class MyOwnerDrawLabelProvider extends OwnerDrawLabelProvider {
    private final Tree tree;

    MyOwnerDrawLabelProvider(Tree tree) {
      this.tree = tree;
    }

    protected void erase(Event event, Object element) {
      if ((event.detail & SWT.SELECTED) != 0) {
        GC gc = event.gc;
        Rectangle area = tree.getClientArea();

        /* Paint the selection beyond the end of last column */
        expandRegion(event, tree, gc, area);

        Rectangle rect = event.getBounds();

        Color background = gc.getBackground();

        if (isRedLabel) {
          gc.setBackground(new Color(tree.getDisplay(), 255, 0, 0));
          gc.fillRectangle(0, rect.y, area.width, rect.height);

          /* Restore colors for subsequent drawing */
          gc.setBackground(background);
          event.detail &= ~SWT.SELECTED;
        }
      }
    }

    protected void measure(Event event, Object element) {}

    protected void paint(Event event, Object element) {}

    public void update(ViewerCell cell) {
      cell.setText(cell.getElement().toString());

      if (isRedLabel)
        cell.setForeground(new Color(Display.getDefault(), 255, 0, 0));
    }
  }

  void expandRegion(Event event, Tree tree, GC gc, Rectangle area) {
    int columnCount = tree.getColumnCount();
    if (event.index == columnCount - 1 || columnCount == 0) {
      int width = area.x + area.width - event.x;
      if (width > 0) {
        Region region = new Region();
        gc.getClipping(region);
        region.add(event.x, event.y, width, event.height);
        gc.setClipping(region);
        region.dispose();
      }
    }
  }

  private MyModel createModel() {

    MyModel root = new MyModel(0, null);
    root.counter = 0;

    MyModel tmp;
    for (int i = 1; i < 10; i++) {
      tmp = new MyModel(i, root);
      root.child.add(tmp);
      for (int j = 1; j < i; j++) {
        tmp.child.add(new MyModel(j, tmp));
      }
    }

    return root;
  }

  public static void main(String[] args) {
    Display display = new Display();
    Shell shell = new Shell(display);
    shell.setLayout(new FillLayout());
    new Snippet002TreeViewer(shell);
    shell.open();

    while (!shell.isDisposed()) {
      if (!display.readAndDispatch())
        display.sleep();
    }

    display.dispose();
  }
}

